                <script type="text/javascript">
                $(function() {
                    //checkNotice();
                    //setInterval("checkNotice()", 60000);
                });
                //定时检查新通知
                function checkNotice() {
                    $.ajax({
                        url: '/notification/checknotice',
                        type: "post",
                        dataType: "json",
                        global: false,
                        success: function(result) {
                            if (result.code == 1) {
                                if (result.data.new_all_cnt == 0) {
                                    if ($('#head-notice_all_cnt1').length > 0) {
                                        $('#head-notice_all_cnt1').hide();
                                        $('#head-notice_all_cnt1').html('0');
                                    }
                                    if ($('#head-notice_all_cnt2').length > 0) {
                                        $('#head-notice_all_cnt2').hide();
                                        $('#head-notice_all_cnt2').html('0');
                                    }
                                } else if (result.data.new_all_cnt > 0) {
                                    if ($('#tip-notice_all_cnt').length > 0) {
                                        $('#tip-notice_all_cnt').html(result.data.new_all_cnt);
                                        if ($('#tip-notice_all_cnt').parents('.newsCenter_tit').find('.newDian').length == 0) {
                                            $('#tip-notice_all_cnt').parents('.newsCenter_tit').prepend('<span class="newDian"></span>');
                                        }
                                    }
                                    if ($('#head-notice_all_cnt1').length > 0) {
                                        $('#head-notice_all_cnt1').html(result.data.new_all_cnt);
                                        $('#head-notice_all_cnt1').show();
                                    }
                                    if ($('#head-notice_all_cnt2').length > 0) {
                                        $('#head-notice_all_cnt2').html(result.data.new_all_cnt);
                                        $('#head-notice_all_cnt2').show();
                                    }
                                }
                                if (result.data.new_notification_cnt > 0) {
                                    if ($('#tip-notice_notification_cnt').length > 0) {
                                        $('#tip-notice_notification_cnt').html(result.data.new_notification_cnt);
                                        if (result.data.new_notification_cnt != $('#more-notice_notification_cnt').html() && $('#more-notice_type').html() == 1) {
                                            window.location.reload();
                                        }
                                    }
                                    $('#head_notice_link').attr('href', '/notification');
                                } else {
                                    $("#all_hu").hide();
                                }
                                if (result.data.new_message_cnt > 0) {
                                    if ($('#tip-notice_message_cnt').length > 0) {
                                        $('#tip-notice_message_cnt').html(result.data.new_message_cnt);
                                        if (result.data.new_message_cnt != $('#more-notice_message_cnt').html() && $('#more-notice_type').html() == 2) {
                                            window.location.reload();
                                        }
                                    }
                                    $('#head_notice_link').attr('href', '/message');
                                }
                                if (result.data.new_news == 2) {
                                    $('#head_notice_link').attr('href', '/notification');
                                } else {
                                    $('#head_notice_link').attr('href', '/message');
                                }

                            }
                        }
                    });
                }
                </script>
