<script>
    $(function(){
        var $oldpass = $('#password_old');
        var $newpass = $('#password_new1');
        var $repass = $('#password_new2');
        var $sub = $('#submit');

        
        $sub.click(function(){
            if($oldpass.val() != ''){
                if($newpass.val() !=''){
                    if($repass.val() !=''){ 
                     $.ajax({
                       type: "POST",
                       url: "updatepwd",
                       data: {'pwd':$newpass.val()},
                       success: function(msg){
                            //console.log(msg);
                            //var msg =JSON.parse(msg)
                            //alert(msg)
                            if(msg == 1){
                                function jump(count) {    
                                    window.setTimeout(function(){    
                                        count--;    
                                        if(count > 0) {
                                            $('#sec').html(count+' 后跳转');
                                            $('#reg').attr("href","__SITE__/index/user/login");
                                            $('#reg').innerHTML='登录页面';
                                            $('#regcg').attr("src","__STATIC__/Reception/Picture/smile.jpg");
                                            $("#cg").html('修改成功');
                                            $('.loading').css({"display":"block"});
                                            $('.back').css({"display":"block"}); 
                                            jump(count);    
                                        } else {    
                                            location.href= '__SITE__/index/user/login';
                                        }    
                                    }, 1000);    
                                }    
                                jump(3);
                            }else{
                                function jump1(count) {    
                                    window.setTimeout(function(){    
                                        count--;    
                                        if(count > 0) { 
                                           $('#sec').html(count+' 后跳转');
                                           $('#reg').attr("href","__SITE__/index/user/info")
                                           $('#reg').html('修改密码');
                                           $('#regcg').attr("src","__STATIC__/Reception/Picture/cry.jpg")
                                           $("#cg").html('修改失败');
                                           $('.loading').css({"display":"block"}); 
                                           jump1(count);    
                                       } else {    
                                        location.href= '__SITE__/index/user/info';
                                    }    
                                }, 1000);    
                                }    
                                jump1(3);
                                
                            }
                        }
                    });         
                 }else{
                    $repass.next().html('<font color="red">请输入密码</font>')
                }
            }else{
                $newpass.next().html('<font color="red">请输入密码</font>')
            }
        }else{
            $oldpass.next().html('<font color="red">请输入原始密码</font>')
        }     
    });
        
        function checkaPassword() {
            var reg = /\w{6,20}/;
            if (reg.test($repass.val())) {
                $repass.next().html('<font color="green">合法的密码</font>');
                if($newpass.val() == $repass.val()){
                    $repass.next().html('<font color="green">两次密码相同</font>');
                    return true;
                } else {
                    $repass.next().html('<font color="red">两次密码输入请保持相同</font>');
                    return false;
                }
            } else {
                $repass.next().html('<font color="red">密码必须是6~20位的字符</font>');
                return false;
            }
        }
        $repass.blur(function(){
            checkaPassword();
        })
        function checkPassword() {
            var reg = /\w{6,12}/;
            if (reg.test($newpass.val())) {
                $newpass.next().html('<font color="green">合法的密码</font>');
                return true;
            } else {
                $newpass.next().html('<font color="red">密码必须是6~12位的字符</font>');
                return false;
            }
        }
        $newpass.blur(function(){
            checkPassword();
        })
        function checkopass(){
           $.ajax({
               type: "POST",
               url: "dopwd",
               data: {'pwd':$oldpass.val()},
               success: function(msg){
                //console.log(msg);
                //var msg =JSON.parse(msg)
                //alert(msg)
                if(msg ==4){
                 $oldpass.next().html('<font color="green">原始密码正确</font>');
                 
             }else{
               $oldpass.next().html('<font color="red">密码错误，请重新输入</font>');
               
           }
       }
   });
       }
       $oldpass.blur(function(){
        checkopass();
    })
       
   })
</script>


